import React, { useState, useEffect } from "react";

export const Three_TabNevBar_TabPart = () => {

    const [Highlight_List, update_Hightlight] = useState([]) 
    useEffect(() => {
        const TotalTabList = document.getElementById('Three_TabNevBar_TabPart')

        console.log(TotalTabList.childNodes[0])

    },[])

    const Activate_Highlight = (tab) => {

        // console.log(tab.target.value)

        if(!Highlight_List.includes(tab.target.value)){
        tab.target.style.fontSize = '7vw'
        update_Hightlight( [...Highlight_List ,tab] )}
    
    }

    return(
        <React.Fragment>

        <div id="Three_TabNevBar_TabPart">
         <li onClick={Activate_Highlight}> Supports </li>
         <li onClick={Activate_Highlight}> How to Use </li>
         <li onClick={Activate_Highlight}> Purpose </li>
        </div>

        </React.Fragment>
        
    )

}

export default Three_TabNevBar_TabPart
