import React, { useEffect, useState } from "react";
import axios from 'axios'

export const UserState_expresser = (props) => {

  const [ UserState, updatestate ] = useState()
  const [ Expresser, updateexpresser ] = useState()

  useEffect(()=>{
    axios.get(
    // api url이나 session cookie 로, 회원데이터 로딩 및 확인
    'https://dummyjson.com/users/1'
    ).then((result)=>{

      switch(typeof result.data){

        case 'null' : 
          updatestate('Stranger');
        case 'object' :
          updatestate('User');
          return result.data.username;
      }
      return result.data.username
    }).then((result)=>{

      switch(UserState){
        case 'Stranger' : updateexpresser('Join us with new account!');
        case 'User' : updateexpresser(`Hello user ${result} !`);
      }

    })
  },[])

  const ChangeState_Of_Expresser = () => {
            switch(UserState){
              case 'Stranger' : updateexpresser(`Welcome & Goin Us`)
              case 'User' : console.log(this)
            }

  }

  return (

    <React.Fragment>

      <div className="UserState_expresser Account_nevbar_child_components" onClick={ChangeState_Of_Expresser}>
      {Expresser}
      </div>

    </React.Fragment>
  )
}

export default UserState_expresser
