import React, { useEffect, useState } from "react";
import axios from 'axios'

export const UserState_expresser = (props) => {

  const [ UserState, updatestate ] = useState()
  const [ Expresser, updateexpresser ] = useState()

  useEffect(()=>{
    axios.get(
    // api url이나 session cookie 로, 회원데이터 로딩 및 확인
    'https://dummyjson.com/users/1'
    ).then((result)=>{

      switch(typeof result.data){
        case null : updatestate('Stranger')
        break
        case Object : updatestate('User')
        break
      }
    }).then(()=>{

      switch(UserState){
        case 'User': updateexpresser('User')
        break
        case 'Stranger': updateexpresser('Stranger')
      }
    })
  },[])

  const ChangeState_Of_Expresser = () => {
            switch(Expresser){
              case 'Stranger' : updateexpresser(`Welcome & Goin Us`)
              break
              case 'User' : updateexpresser()
            }

  }

  return (

    <React.Fragment>

      <div className="UserState_expresser Account_nevbar_child_components">
      {Expresser}
      </div>

    </React.Fragment>
  )
}

export default UserState_expresser
