import React, { useEffect, useState } from "react";

import {APIProvider, Map, Marker, useMapsLibrary,useApiIsLoaded, useMap} from '@vis.gl/react-google-maps'

export const Google_Map = () => {

const INITIAL_CAMERA = {
        zoom : 15,
        center : {lat: 0, lng: 0}
    }

const INITIAL_MARKER = {
        position : {lat: 0, lng: 0}
    }

const [cameraProps, setCameraProps] = useState(INITIAL_CAMERA);
const [markerProps, setMarkerProps] = useState(INITIAL_MARKER);

const Map_Instance = useMap('Google_Map');
const Place_Library = useMapsLibrary('places');

    const UpdateMap = (latitude, longitude) => {

        setMarkerProps({
            position: {lat: latitude, lng: longitude}
        })

        setCameraProps({
            zoom: 17, 
            center: {lat: latitude, lng: longitude}
        })

    }

    const Location_Loaded_Success_CallBack = (position) => {
        let latitude = position.coords.latitude;
        let longitude = position.coords.longitude;

        UpdateMap(latitude,longitude)
    }

    const Current_Location_Loading = () => {
            if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                
            Location_Loaded_Success_CallBack
    
            )}
    }

    const apiIsLoaded = useApiIsLoaded();

    useEffect(()=>{

        Current_Location_Loading()
        if (!apiIsLoaded) return;
        const svc = new Place_Library.PlacesService(Map_Instance);
        
    },[])

return <React.Fragment>

            <APIProvider apiKey={`${process.env.REACT_APP_API}`}>

                <Map id='Google_Map'
                
                    style={ 
                        { width:'inherit',
                        height:'20vh',
                        backgroundColor:'white',
                        margin: '3vh 6vw 0 10vw',
                        border: 'black solid 5px'
                        }}
                    
                    {...cameraProps}>
                
                    <Marker
                    {...markerProps}/>

                </Map>

            </APIProvider>


</React.Fragment>

}

export default Google_Map
