  if (zoom >= 15.5) {
    return 67.5;
  }

  // range [10...14]
  if (zoom <= 14) {
    return mapLinear(zoom, 10, 14, 30, 45);
  }

  // range [14...15.5]
  return mapLinear(zoom, 14, 15.5, 45, 67.5);
