export const limitTiltRange = ({viewState}: any) => {
  const pitch = viewState.pitch;
  const gmZoom = viewState.zoom + 1;
  const maxTilt = getMapMaxTilt(gmZoom);

  return {...viewState, fovy: 25, pitch: Math.min(maxTilt, pitch)};
};
