import React, {useState} from "react";

import Curved_Arrow from '../../../1_image_or_icon/curved_arrow-icon.png'
import Homepage_Btn from "./Homepage_Btn.js";

import {Buffer} from 'buffer';

export const My_Account = (props) => {

    // console.log('my account', props.id)
    const [ EditOrRead, updateStatus ] = useState(false)

    const Default_Info_Dispenser = (section,input,type,FontSizeOption,edit_status) => {
        
        switch(edit_status){

        case(true):
        return <React.Fragment>
        <div
            className="MyAccount_Dispenser_Container">
                    <span
                    style={{
                        fontSize: '5.5vw'
                    }}
                    > 
                    {section}
                    </span>

                    <div>
                        <img
                        src={Curved_Arrow}
                        style={{
                            width:'5.5vw',
                            transform: 'rotate(270deg)'
                        }}>
                        </img>

                        <input
                        style={{
                            fontSize: FontSizeOption,
                            position: "relative",
                            left: '0.5vw',
                            
                            width: '30vw',
                            margin: '1vh',
                            padding: '1vh 0vw 1vh 2.5vw',
                            
                            borderRadius: '15px'
                        }}
                        type={type}
                        defaultValue={input}
                        >
                        
                        </input>
                    </div>

        </div></React.Fragment>
        
        case(false):
        return <div
            className="MyAccount_Dispenser_Container">
                    <span
                    style={{
                        fontSize: '5.5vw'
                    }}
                    > {section}
                    </span>

                    <div>
                        <img
                        src={Curved_Arrow}
                        style={{
                            width:'5.5vw',
                            transform: 'rotate(270deg)'
                        }}>
                        </img>

                        <span
                        style={{
                            fontSize: FontSizeOption,
                            position: "relative",
                            left: '1.5vw',
                            bottom: '0.5vh'
                        }}
                        >
                        {input}
                        </span>
                    </div>

        </div>

        default:
        return;

        }
    }

    const Default_Info_Dispenser_Position = (section,input,FontSizeOption,edit_status) => {

        console.log(props.id.userinfo.position)
        
        switch(edit_status){

        case(true):
        return <React.Fragment>
        <div
            className="MyAccount_Dispenser_Container">
                    <span
                    style={{
                        fontSize: '5.5vw'
                    }}
                    > 
                    {section}
                    </span>

                    <div>
                        <img
                        src={Curved_Arrow}
                        style={{
                            width:'5.5vw',
                            transform: 'rotate(270deg)'
                        }}>
                        </img>

                        

                        <select
                        style={{
                            fontSize: FontSizeOption,
                            position: "relative",
                            left: '0.5vw',
                            
                            width: '30vw',
                            margin: '1vh',
                            padding: '1vh 0vw 1vh 2.5vw',
                            
                            borderRadius: '15px'
                        }}
                        >
                         <option value={props.id.userinfo.position} disabled hidden selected>{props.id.userinfo.position}</option>
                         <option value={'business'}>business</option>
                         <option value={'reviewer'}>reviewer</option> 
                        </select>
                        
                        
                    </div>

        </div></React.Fragment>
        
        case(false):
        return <div
            className="MyAccount_Dispenser_Container">
                    <span
                    style={{
                        fontSize: '5.5vw'
                    }}
                    > {section}
                    </span>

                    <div>
                        <img
                        src={Curved_Arrow}
                        style={{
                            width:'5.5vw',
                            transform: 'rotate(270deg)'
                        }}>
                        </img>

                        <span
                        style={{
                            fontSize: FontSizeOption,
                            position: "relative",
                            left: '1.5vw',
                            bottom: '0.5vh'
                        }}
                        >
                        {input}
                        </span>
                    </div>

        </div>

        default:
        return;

        }
    }

    switch(typeof props.id){

    case('undefined'):
    return <div>'wrong access'</div>

    case('object'):
        switch(props.id.status){
            case('verified'):
            const UserData = props.id.userinfo

            const rawdata = UserData.picture.data
            const UserImage_Data = Buffer.from(rawdata)
            const base64Image = UserImage_Data.toString('base64')
            const imgsrc = `data:image/jpg;base64,${base64Image}`;

            return <React.Fragment>

                <div
                className="Personalize_Data_Page_Container"
                >

                <Homepage_Btn/>

                <div
                className="Personalized_Data_Container_Aligner"
                >

                <span
                className="Personalized_Data_Page_TextTitle"
                >
                My Account
                </span>

                <div
                className="MyAccount_Dispenser_Container"
                >

                    <span
                    style={{
                        fontSize: '5.5vw'
                    }}
                    > Profile_Image
                    </span>

                    <div>
                        <img
                        src={imgsrc}
                        style={{
                            maxWidth: '20vw',
                            objectFit: 'cover',
                            borderRadius: '10px',
                            border: 'black solid 3px',
                            margin: '0.8vh 0vw',
                            position: 'relative',
                            top: '1vh',
                            left: '0vw'
                        }}
                        onTouchStart={(e)=>{
                            e.target.style.maxWidth= '60vw'
                        }}
                        onTouchEnd={(e)=>{
                            e.target.style.maxWidth= '20vw'
                        }}
                        >
                        
                        </img>
                    </div>

                </div>

                {Default_Info_Dispenser('Name',`${UserData.name}`, 'text', '5.5vw', EditOrRead)}

                {Default_Info_Dispenser('Email_Address', `${UserData.email}`, 'email', '4vw', EditOrRead)}

                {Default_Info_Dispenser('Password', `${UserData.password}`, 'password','4vw', EditOrRead)}
                
                {Default_Info_Dispenser_Position('Position', `${UserData.position}`,'4vw',EditOrRead)}

                <div
                    style={{
                        display: 'grid',
                        placeItems: 'center',
                       
                        width: 'inherit',
                        minHeight:'5vh',

                        margin: '1vh 0vw',

                        border: 'black solid 3px',
                        borderRadius: '13px',

                        backgroundColor: '#e8e8e8',
                        
                        fontSize: '6vw',
                        letterSpacing: '0.5vw'
                    }}
                >
                    <span
                    onClick={
                    ()=>{
                        updateStatus(true)
                    
                    }
                    }
                    >
                    Edit
                    </span>
                </div>

                </div>

                

                </div>

            </React.Fragment>

            case('unverified'):
                console.log('unverified')
                return <React.Fragment>

                <div
                className="Personalize_Data_Page_Container"
                >

                    <Homepage_Btn/>

                <div
                className="Personalized_Data_Container_Aligner"
                >

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    My Account
                    </span>

                    <br></br>

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    This Page
                    <br></br>
                    Need Login
                    </span>

                </div>

                </div>

            </React.Fragment>
            
        }
    }

}

export default My_Account
