import React, { useEffect, useRef, useState } from "react";

import DateCalculator from "./DateCalculator";

const Single_ReviewData = ({target}) => {

    console.log(target)
    const containerRef = useRef(null)
    const [FontSize, updateFONTSIZE] = useState({})
    const [WIDTH, updateWIDTH] = useState({fliped: false})

    useEffect(()=>{

        const divElement = containerRef.current
        const resizeObserver = new ResizeObserver(()=>{
            if(divElement){
                console.log(divElement.offsetWidth)
                updateFONTSIZE({
                    title : `${divElement.offsetWidth*0.09}px`,
                    content : `${divElement.offsetWidth*0.065}px`
                })
                updateWIDTH({
                    textbox : `${divElement.offsetWidth*0.18}vw`,
                    fliped : true
                })
            }
        })
    
        if(divElement){
            resizeObserver.observe(divElement)
        }

        return()=>{
            resizeObserver.disconnect()
        }

    },[containerRef])

    const start_dispenser = (input) => {
        let container = [];
        for(let i=0; i<input; i++){
            container.push("\u2B50")
        }
        return <span
        style={{
            width: '80%',
            display: 'flex',
            justifyContent: 'space-around',
            border: 'white solid 2px',
            borderRadius: '15px',
            padding: '0.4vh 2vw 1vh 2vw',
            }}
        >
            {container.map((element)=>{
                return <span>{element}</span>
            })}
        </span>
        
    }

    return <React.Fragment>

        <div
        style={{
            width:'100%',
            height: '80%',
            marginLeft: '30px',
            display:'grid',
            gridAutoFlow: 'row',
            rowGap: '10px',
            alignItems: 'center',
            justifyItems: 'center',
            padding: '30px 10px',
            fontFamily: '큐트신민상',
            transition: 'all 0.3s ease-in-out',

            border: 'black solid 2.5px',
            borderRadius: '30px'
        }}
        ref={containerRef}
        >
            <div
            style={{
                justifySelf:'center',
                fontSize: FontSize.title
            }}
            >
            {target.placename}
            </div>

            <textarea
            style={{
                width: WIDTH.textbox,
                height: '30vh',
                letterSpacing: '3.5px',
                wordSpacing: '1px',
                lineHeight: '3.5vh',
                whiteSpace: 'pre-wrap',
                overflowWrap: 'break-word',
                justifySelf: 'center',
                fontSize: FontSize.content,
                backgroundColor: 'white',
                padding: '10px',
                borderRadius: '20px'
            }}
            >
            {target.user_post_text}
            </textarea>

            <div
            style={{
                justifySelf:'center',
                fontSize: FontSize.title
            }}
            >
            작성자: {target.user.name}
            </div>
            

            <div>Created</div>

            <DateCalculator
            date={target.created_at}
            />
            

            <div
            style={{
                width: '90%',
                display: 'grid',
                gridTemplateColumns: '1fr 1fr 1fr',
                rowGap: '1vw',
                border: 'white solid 3px',
                justifyItems: 'center',
                padding: '10px 5px'
            }}
            >
                {target.picture_url_array.map((url)=>{
                    return <img
                    src={url}
                    style={{
                        width: '10vw',
                        aspectRatio: '1/1',
                        objectFit: 'cover',
                        border:'white solid 3px',
                        borderRadius: '10px',
                    }}
                    >
                    </img>
                })}

            </div>

            <div>
                
                {start_dispenser(target.star_rating)}
            
            </div>

            

        </div>

    </React.Fragment>



}

export default Single_ReviewData
