import React, { useState } from "react";

import Post_Video_btn from './Post_Video'
import Post_Review_Btn from "./Post_reviews";
import Post_Journey_Btn from "./Post_Journey";

export const Three_Option_integrator = () => {

    const [ BtnState, updateBox ] = useState({
        Post_review_box_activated : false,
        Post_video_box_activated : false,
        Post_Journey_box_activated : false

    })

    const post_review_btnEvent = (target, value) =>{
        let Renewed_State = {...BtnState}
        Renewed_State[target] = value
        
        updateBox({...Renewed_State})
        console.log(Renewed_State)
    }

    return <React.Fragment>

    <div
    style={{
        display: "grid",
        gridRowGap: '2.5vh',
        marginTop: '40vh'
    }}
    >
    
    <Post_Video_btn
    clickEvent={()=>{post_review_btnEvent('Post_video_box_activated',true)}}
    />

    <Post_Review_Btn
    clickEvent={()=>{post_review_btnEvent('Post_review_box_activated',true)}}
    />

    <Post_Journey_Btn
    clickEvent={()=>{post_review_btnEvent('Post_journey_box_activated',true)}}
    />
    
    </div>

    <br></br>
    </React.Fragment>

}

export default Three_Option_integrator
