import { createStore } from "redux";

export let Url = new URLSearchParams(document.location.search)

export const initial_State = {
    urlObject : Url,
    urlToString : 'Empty'
}

export function AppReducer(state = initial_State, action){

    switch(action.type){

        case 'UpdateUrl':{
            state.urlObject = state.urlObject.set(action.option, action.param)
            return {...state};
        }

        case 'UpdateUrlString':{
            state.urlToString = action.param
            return {...state};
        }

        case 'ReturnUrlString':{
            return {...state};
        }

        
        default: return {...state};
    }

}

export const store = createStore(AppReducer)

export const ActionCreater = (Applied_type, Applied_option ,Applied_param) => {
    return {
        type: Applied_type,
        option: Applied_option,
        param : Applied_param}
}

export default {AppReducer, store}
