import React, { useEffect, useRef } from "react";

const Single_ReviewData = ({target}) => {

    console.log(target)
    const containerRef = useRef(null)

    useEffect(()=>{
        if(containerRef.current){
        console.log(containerRef.current.offsetWidth)}
    },[containerRef.current.offsetWidth])

    return <React.Fragment>

        <div
        style={{
            width:'70%',
            marginLeft: '30px',
            display:'grid',
            gridAutoFlow: 'row',
            alignItems: 'center',

            border: 'black solid 2.5px',
            borderRadius: '30px'
        }}
        ref={containerRef}
        >
            <div
            style={{
                justifySelf:'center',
                // fontSize:''
            }}
            >
            {target.placename}
            </div>

            <div
            style={{
                width:'50vw',
                letterSpacing: '1.5px',
                wordSpacing: '1px',
                lineHeight: '3.5vh',
                whiteSpace: 'pre-wrap',
                overflowWrap: 'break-word',
                justifySelf: 'center',
                // fontSize: ''
            }}
            >
            {target.user_post_text}
            </div>

            <div
            style={{justifySelf:'center'}}
            >
            작성자: {target.user.name}
            </div>
            
            <div
            style={{
                width: '70%',
                display: 'grid',
                gridAutoFlow: 'row',
                border: 'white solid 3px',
                justifySelf: 'center',
                padding: '10px'
            }}
            >
                {target.picture_url_array.map((url)=>{
                    return <img
                    src={url}
                    style={{
                        width: '10vw',
                        aspectRatio: '1/1',
                        objectFit: 'cover',
                        border:'white solid 3px',
                        borderRadius: '10px',
                    }}
                    >
                    </img>
                })}

            </div>

            

        </div>

    </React.Fragment>



}

export default Single_ReviewData
