import React from "react";

import Curved_Arrow from '../../../1_image_or_icon/curved_arrow-icon.png'
import Profile_Stranger from '../../../1_image_or_icon/Profile_Stranger_icon.jpg'

export const My_Account = (props) => {

    console.log('my account', props.id)
    const UserData = props.id.userinfo

    const Default_Info_Dispenser = (section,input,FontSizeOption) => {
        return <div
            className="MyAccount_Dispenser_Container"
        >

                    <span
                    style={{
                        fontSize: '5.5vw'
                    }}
                    > {section}
                    </span>

            <div>
                <img
                src={Curved_Arrow}
                style={{
                    width:'5.5vw',
                    transform: 'rotate(270deg)'
                }}
                >
                </img>
                <span
                style={{
                    fontSize: FontSizeOption,
                    position: "relative",
                    left: '1.5vw',
                    bottom: '0.5vh'
                }}
                >
                {input}
                </span>
            </div>

        </div>
    }

    switch(typeof props.id){

    case('undefined'):
    return <div>'wrong access'</div>

    case('object'):
        switch(props.id.status){
            case('verified'):
            return <React.Fragment>

                <div
                className="Personalize_Data_Page_Container"
                >

                <a href="/search">
                go home
                </a>

                <div
                className="Personalized_Data_Container_Aligner"
                >

                

                <span
                className="Personalized_Data_Page_TextTitle"
                >
                My Account
                </span>

                <div
                className="MyAccount_Dispenser_Container"
                >

                    <span
                    style={{
                        fontSize: '5.5vw'
                    }}
                    > Profile_Image
                    </span>

                    <div>
                        
                        <img
                        src={Profile_Stranger}
                        style={{
                            maxWidth: '20vw',
                            objectFit: 'cover',
                            borderRadius: '10px',
                            border: 'black solid 3px',
                            margin: '0.8vh 0vw',
                            position: 'relative',
                            top: '1vh',
                            left: '0vw'
                        }}
                        onTouchStart={(e)=>{
                            e.target.style.maxWidth= '60vw'
                        }}
                        onTouchEnd={(e)=>{
                            e.target.style.maxWidth= '20vw'
                        }}
                        >
                        
                        </img>
                    </div>

                </div>

                {Default_Info_Dispenser('Name',`${UserData.name}`, '5.5vw')}

                {Default_Info_Dispenser('Email_Address', `${UserData.email}`, '4vw')}

                {Default_Info_Dispenser('Password', `${UserData.password}`)}
                
                {Default_Info_Dispenser('Position', `${UserData.position}`)}

                <div
                    style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent: 'center',
                        alignItems: 'center',

                        width: 'inherit',
                        minHeight:'5vh',

                        margin: '1vh 0vw',

                        border: 'black solid 3px',
                        borderRadius: '13px',

                        backgroundColor: '#e8e8e8',
                        
                        fontSize: '6vw',
                        letterSpacing: '0.5vw'
                    }}
                >
                    <span>
                    Edit
                    </span>
                </div>

                </div>

                

                </div>

            </React.Fragment>
        }
    }

}

export default My_Account
