import React, {useRef, useState, useEffect} from "react"
import styled from "styled-components"

const Delete_popup_container = styled.div`
    position: relative;
    display: ${props => props.activate ? 'block' : 'none'};
`

const Delete_popup = (props) => {

    const selfRef = useRef(null)
    // console.log(props)

    useEffect(()=>{
        if(!selfRef.current){
            console.log("selfRef is undefined")
            return;
        }
        
        const previousSibling = selfRef.current.previousElementSibling;
        if(previousSibling && previousSibling.children[0]){
            const Target_reviewElement = previousSibling.children[0];
            
            // 현재 요소의 복제본 생성
            const popupContent = selfRef.current.innerHTML;
            const newPopup = document.createElement('div');
            newPopup.innerHTML = popupContent;
            newPopup.className = selfRef.current.className;
            newPopup.style.cssText = window.getComputedStyle(selfRef.current).cssText;
            
            // 스타일 속성 복사
            newPopup.style.position = 'relative';
            newPopup.style.display = props.activate ? 'block' : 'none';
            
            // 기존 팝업 제거 후 새 위치에 추가
            Target_reviewElement.appendChild(newPopup);
            if(selfRef.current.parentNode) {
                selfRef.current.style.display = 'none';
            }
        }
    },[props.activate]) // props.activate가 변경될 때마다 실행

    return <React.Fragment>

    <Delete_popup_container
        activate={props.activate}
        ref={selfRef}
    >
        <span>Are you sure to delete this review?</span>
    </Delete_popup_container>

    </React.Fragment>

}

export default Delete_popup
