import React from "react";

import Keyword_popup from "./Keyword_popup";

export const Filter_Option_Selector = (props) => {

    const Showing_popup = () => {

        const Keyword = document.getElementById('Keyword_popup_Closed')
        Keyword.classList = 'Keyword_popup'

    }

    return <React.Fragment>

        <div className="OptionSelector">

            <span className="OptionRepresented">{props.option}</span>
            
            <br></br>
            <div id={`AddedOption_${props.option}`}></div>
            <div className="OptionSelector_Button" onClick={()=>{

                        Showing_popup()

            }}>+</div>

        </div>

        <Keyword_popup option={props.option}/>

    </React.Fragment>

}

export default Filter_Option_Selector
