import React, { useEffect, useState } from "react";

import Homepage_Btn from './Homepage_Btn.js'
import DateCalculator from "./DateCalculator.js";

import { Buffer } from "buffer";

export const My_Reviews = (props) => {

    const [REVIEWS ,updateREVIEWS] = useState([])
    const [USERNAME ,updateUSERNAME] = useState({name: ''})

    useEffect(()=>{

        let USER;
        let STATUS;
        
        if(props.id){
            USER = props.id.userinfo
            STATUS = props.id.status
        }

        switch(STATUS){
            case('unverified'):
            return;

            case('verified'):
            Load_Review(USER.id,USER.name)
            return;

            default:
            return;
        }

    },[props])

    const TextDispenser = (props) => {

        let TextPrettier;
        const TextContent = props.element.user_post_text

        for(let index=0; index++; index<TextContent.length){
            if(index%9 === 0){
                const SLICE = TextContent.slice(index, index+9)
                TextPrettier = <React.Fragment>

                {TextPrettier}
                {SLICE} <br></br>

            </React.Fragment>
            }
            if(index === 0){
                const SLICE = TextContent.slice(index, index+9)
                TextPrettier = <React.Fragment>

                    {SLICE} <br></br>

                </React.Fragment>
            }
        }

        return <div
        style={{
            backgroundColor: 'white',
            borderRadius: '10px',

            width: '80%',
            padding: '1vh 2.5vw',

            justifySelf: 'center',
            
            letterSpacing: '1.5px',
            wordSpacing: '-3px',
            lineHeight: '3.5vh',
            whiteSpace: 'normal'
        }}
        >
            {props.element.user_post_text}
        </div>
    }

    const Review_Container = () => {

        return <React.Fragment>

            <div
            style={{
                width:'95%',

                display: 'grid',
                gridTemplateColumns: '(repeat 1, 1fr)',
                rowGap: '2vh',

                padding: '2vh 0vw'
            }}
            >

            {REVIEWS.map((element, index)=>{

                const PICTURES_BINARY_ARRAY = element.user_post_pictures
                let PICTURES_ARRAY = [];

                PICTURES_BINARY_ARRAY.forEach((element, index)=>{
                    const DATA = Buffer.from(element)
                    let encoded_imagesrc = DATA.toString('base64')
                    encoded_imagesrc = `data:image/jpg;base64,${encoded_imagesrc}`;

                    PICTURES_ARRAY = [...PICTURES_ARRAY, encoded_imagesrc]
                })

                return <div
                style={{
                    display: 'grid',
                    gridTemplateColumns: '(repeat 1, 1fr)',
                    rowGap: '1vh',

                    border:'black solid 3px',
                    borderRadius: '28px',

                    padding: '1.5vh 3vw 3vh 3vw'
                }}
                >
                
                <TextDispenser
                element={element}
                />

                <div
                style={{
                    position:'relative',
                    right: '14px',
                    justifySelf: 'end'
                }}
                >
                    {`작성자: ${USERNAME.name}`}
                </div>

                <div
                style={{
                    position:'relative',
                    right: '14px',
                    justifySelf: 'end'
                }}
                >
                    Created
                </div>

                <div
                style={{
                    position:'relative',
                    right: '14px',
                    bottom: '7px',

                    justifySelf: 'end'
                }}
                >
                    <DateCalculator
                    date={element.created_at}
                    />
                </div>

                <div
                style={{
                    
                    display: 'grid',
                    gridTemplateColumns: 'repeat(3, 1fr)',
                    justifyItems: 'center',
                    alignItems: 'center'
                }}
                >
                    {PICTURES_ARRAY.map((element, index)=>{

                        return <img
                        src={`${element}`}
                        style={{
                            width: '10vw'
                        }}

                        >
                        </img>

                    })}
                </div>

                </div>

            })}

            </div>

        </React.Fragment>

    }

    const Load_Review = async (user_id, username) => {
        await fetch(`/usercreation/read_reviews/${user_id}`).then( async (result)=>{
            const review_data = await result.json()
            updateREVIEWS(review_data.result)
            updateUSERNAME({name: username})
        })
    }

    if(props.id){
        
        switch(props.id.status){
        case('verified'):
        return <React.Fragment>

        <div
        className="Personalize_Data_Page_Container"
        >

            <Homepage_Btn/>

            <div
            className="Personalized_Data_Container_Aligner"
            >

                <span
                className="Personalized_Data_Page_TextTitle"
                >
                My Reviews
                </span>
                <br></br>

            
                <Review_Container/>
                

            </div>

        </div>

    </React.Fragment>

    case('unverified'):
    return <React.Fragment>

            <div
            className="Personalize_Data_Page_Container"
            >

                <Homepage_Btn/>

                <div
                className="Personalized_Data_Container_Aligner"
                >

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    My Reviews
                    </span>
                    <br></br>

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    This Page
                    <br></br>
                    is Preparing
                    </span>                    

                </div>

            </div>

        </React.Fragment>


    }


    }
}

export default My_Reviews
