import React, { useEffect, useRef, useState } from "react";

import DateCalculator from "./DateCalculator";

const Single_ReviewData = ({target}) => {

    const containerRef = useRef(null)
    const [FontSize, updateFONTSIZE] = useState({})
    const [WIDTH, updateWIDTH] = useState({fliped: false})

    useEffect(()=>{

        const divElement = containerRef.current
        const resizeObserver = new ResizeObserver(()=>{
            if(divElement){
                console.log(divElement.offsetWidth)
                updateFONTSIZE({
                    title : `${divElement.offsetWidth*0.09}px`,
                    content : `${divElement.offsetWidth*0.065}px`
                })
                updateWIDTH({
                    textbox : `${divElement.offsetWidth*0.18}vw`,
                    fliped : true
                })
            }
        })
    
        if(divElement){
            resizeObserver.observe(divElement)
        }

        return()=>{
            resizeObserver.disconnect()
        }

    },[containerRef])

    return <React.Fragment>

        <div
        style={{
            width:'100%',
            height: '80%',
            marginLeft: '30px',
            display:'grid',
            gridAutoFlow: 'row',
            rowGap: '0',
            alignItems: 'center',
            padding: '0px 10px',
            fontFamily: '큐트신민상',
            transition: 'all 0.3s ease-in-out',

            border: 'black solid 2.5px',
            borderRadius: '30px'
        }}
        ref={containerRef}
        >
            <div
            style={{
                justifySelf:'center',
                fontSize: FontSize.title
            }}
            >
            {target.placename}
            </div>

            <textarea
            style={{
                width: WIDTH.textbox,
                height: '100%',
                letterSpacing: '3.5px',
                wordSpacing: '1px',
                lineHeight: '3.5vh',
                whiteSpace: 'pre-wrap',
                overflowWrap: 'break-word',
                justifySelf: 'center',
                fontSize: FontSize.content,
                backgroundColor: 'white',
                padding: '10px',
                borderRadius: '20px'
            }}
            >
            {target.user_post_text}
            </textarea>

            <div
            style={{
                justifySelf:'center',
                fontSize: FontSize.title
            }}
            >
            작성자: {target.user.name}
            </div>
            
            <div
            style={{
                justifySelf: 'center'
            }}
            >

            <DateCalculator
            date={target.created_at}
            />

            </div>

            <div
            style={{
                width: '70%',
                display: 'grid',
                gridAutoFlow: 'row',
                border: 'white solid 3px',
                justifySelf: 'center',
                padding: '10px'
            }}
            >
                {target.picture_url_array.map((url)=>{
                    return <img
                    src={url}
                    style={{
                        width: '10vw',
                        aspectRatio: '1/1',
                        objectFit: 'cover',
                        border:'white solid 3px',
                        borderRadius: '10px',
                    }}
                    >
                    </img>
                })}

            </div>

            

        </div>

    </React.Fragment>



}

export default Single_ReviewData
