import React from "react";
import { useState , useRef } from "react";
import { useSelector } from "react-redux";

import GlassIcon from '../../../../1_image_or_icon/reading_glass_icon.png'

export const SearchBar = () => {

    const SearchBar_Ref = useRef('')
    const [ Search_Keyword, updateKeyword ] = useState('')

    const Url = useSelector(state => state)

    return <React.Fragment>

        <form
            id='SearchBar_Container'
            action="/search"
            method='GET'>

            <div id='SearchBar'>

                <input
                    id="SearchBar_Input"
                    ref={SearchBar_Ref}
                    name='keyword'
                    onChange={
                        ()=>{
                            Url.urlObject.set('keyword',`${SearchBar}`)
                        }
                    }>
                </input>

                <a
                id = 'Search_Keyword_&_Options' 
                href={`/search${Url.urlObject.toString()}`}>
                <img id="Reading_Glass_icon" src={GlassIcon}>
                </img>
                </a>

            </div>


        </form>

    </React.Fragment>

}

export default SearchBar;
