import React, {useState, useRef, useEffect} from "react";
import {useLocation} from 'react-router-dom'

const Review_Post_Box = (props) =>{

    const [text, setText] = useState('');
    const [pictures, setPictures] = useState([]);
    const textareaRef = useRef(null);
    
    useEffect(()=>{
        if (textareaRef.current) {
            textareaRef.current.scrollTop = textareaRef.current.scrollHeight;
          }
    },[text])

    const location = useLocation();

    const handleChange = (e) => {
        setText(e.target.value); 
      };

    const handleSubmit = async (e) => {
        e.preventDefault();

        let ID = await fetch('/login_check',{
            method: 'GET',
            headers:{
                'Content-Type': 'application/json'
            }
        })

        let ID_data = await ID.json()

        switch(ID_data.status){

        case('verified'):
            const formData = new FormData(e.target)
            const placecode = location.pathname.slice(17)
            formData.append('id',ID_data.userinfo.id)
            formData.append('placecode', placecode)
            
            const POST_ACTION = await fetch('/post_review',{
                method:`POST`,
                body: formData
            })
            
        case('unverified'):
            alert(`You need to login before you write down`)
            break;

        default:
            alert(`You need to login before you write down`)
            break;

        }
    }

        const picture_status = (input) => {
            if(input.length === 0){ 
                return <span>
                Click Here
                <br></br>

                <br></br>
                To Add Picture
                </span>
             }
            return <React.Fragment>

                <div>
                    {/* {input.map((element)=>{
                        return <image
                            src=''
                        >
                        </image>
                    })} */}


                </div>

            </React.Fragment>
        }

    return <React.Fragment>
        
        <div
        style={{
            width: 'inherit',
            height: 'fit-content',

            border: 'black solid 2.5px',
            borderBottom: 'black solid 6px',

            borderRadius: '15px',
            padding: '2vh 5vw',

            fontSize: '20px'

        }}
        >

            <form
            onSubmit={handleSubmit}
            >

            <label
            style={{
                width:'95%',
                height:'15vh',

                display:'grid',
                justifyContent: 'center',
                alignItems:"center",

                border: 'black solid 2px',
                borderBottom: 'black solid 5px',
                borderRadius: '15px',

                textAlign: 'center',

                background: '#f5ee90',
                fontFamily: '큐트신민상'
            }}
            htmlFor="post_pictures"
            >
                {picture_status}
            </label>
            
            <input
            id="post_pictures"
            name="post_pictures"
            type="file"
            multiple
            accept="image/*"

            required
            onChange={(e)=>{
                const files = e.target.files

                if(files.length > 0){
                    Array.from(files).forEach((file) => {
                        const reader = new FileReader()
                        reader.onload = function(e){
                            const imageData = e.target.result;
                            setPictures([...pictures, imageData])
                        }
                    reader.readAsDataURL(file)
                    })
                }
            }}
            style={{
            display:"none"
            }}
            >
            </input>

            <textarea
            name="post_text"
            style={{
                height:'20vh',

                marginTop: '3vh',
                fontSize: '15px',

                padding: `3vh 6vw`,

                border: 'black solid 2px',
                borderBottom: 'black solid 5px',
                borderRadius: '15px',

                backgroundColor: '#f2eeb8',

                letterSpacing: '1.5px',
                wordSpacing: '-3px',
                lineHeight: '3.5vh'
            }}
            placeholder="Write down your comment at here"
            
            ref={textareaRef}
            value={text}
            onChange={handleChange}
            required
            >
            
            </textarea>
        
            <input
            type="submit"
            value={'Post it'}
            style={{
                width:'100%',
                
                fontFamily: '큐트신민상',
                fontSize: '5vw',
                fontWeight: 'bold',
                letterSpacing: '2vw',

                borderBottom: 'black solid 5px',

                marginTop: '1vh',
                padding: '1.5vh 0vw',

                borderRadius: '10px',
                
            }}
            >
            
            </input>
            </form>

        </div>
        

    </React.Fragment>

}

export default Review_Post_Box;
