import React, {useRef, useState, useEffect} from "react"
import styled from "styled-components"

const Delete_popup_container = styled.div`
    position: relative;
    display: ${props => props.activate ? 'block' : 'none'};
`

const Delete_popup = (props) => {

    const selfRef = useRef(null)
    // console.log(props)

    useEffect(()=>{
        if(!selfRef.current){
            console.log("selfRef is undefined")
            return;
        }
        
        const previousSibling = selfRef.current.previousElementSibling;
        if(previousSibling && previousSibling.children[0]){
            const Target_reviewElement = previousSibling.children[0];
            try {
                Target_reviewElement.appendChild(selfRef.current.cloneNode(true));
                if(selfRef.current.parentNode) {
                    selfRef.current.parentNode.removeChild(selfRef.current);
                }
            } catch(error) {
                console.error("Error moving popup:", error);
            }
        }
    },[])

    return <React.Fragment>

    <Delete_popup_container
        activate={props.activate}
        ref={selfRef}
    >
        <span>Are you sure to delete this review?</span>
    </Delete_popup_container>

    </React.Fragment>

}

export default Delete_popup
