import React, { useState } from "react";

export const Closed_Toggler_Button = () => {

    const [OnOff, updateOnnOff] = useState('on')

    const Switch = (status) => {

        switch(status){

            case('on'):
            updateOnnOff('off')
            return;

            case('off'):
            updateOnnOff('on')
            return;
        }

    }

    switch(OnOff){

    case('on'): 
        
        return <React.Fragment>
        <span id='Closed_Toggler_Button' value='' onClick={Switch('on')}>
            Open Filter
        </span>
        </React.Fragment>
    
    case('off'):

        return <React.Fragment>
        <span id='Closed_Toggler_Button' onClick={Switch('off')}>
            Close Filter
        </span>
        </React.Fragment>

    }
}

export default Closed_Toggler_Button;
