import { useState } from "react"

export const DateCalculator = (props) => {

    const [Datedata , updatedata] = useState('')

    const Define_Today = () =>{

        const Today_Y = new Date().getFullYear()
        const Today_M = new Date().getMonth()
        const Today_D = new Date().getDate()
        const Today_T = new Date().toLocaleString('ko-KR',{
            timeZone: 'Asia/Seoul'
        })

        return {
            Year : Today_Y,
            Month : Today_M,
            Date : Today_D,
            Time : Today_T
        }
    }
    
    const Status_Definer = (origin, target) => {
    
        const ORIGIN = Number(origin)
        const TARGET = Number(target)

        if(ORIGIN < TARGET){
           return{
                Gap: TARGET-ORIGIN,
                status: '+',
                message: 'Target_data is ahead then Origin_data'
           }
        }
        if(ORIGIN > TARGET){
            return{
                Gap: ORIGIN-TARGET,
                status: '-',
                message: 'Target_data is older_data then Origin_data'
            }
        }
        if(ORIGIN === TARGET){
            return{
                Gap: 0,
                status: '=',
                message: 'Target_data is same as Origin_data'}
        }
        else{
            return;
        }
    
    
    }
    
    const CompareYMD = async (section, target) =>{
    
        const Today_Data = Define_Today();
    
        switch(section){
            case('Year'):
            return Status_Definer(Today_Data.Year, target);
    
            case('Month'):
            return Status_Definer(Today_Data.Month, target);
    
            case('Date'):
            return Status_Definer(Today_Data.Date, target);
    
            default:
            return;
        }
    }
    
    const CompareProcess = async (target) =>{

        const Yeardata = await CompareYMD('Year', target.Year)
        const Monthdata = await CompareYMD('Month', target.Month)
        const Datedata = await CompareYMD('Date', target.Date)
        const Timedata = await CompareYMD('Time', target.Time)

        return {
            Year: Yeardata,
            Month: Monthdata,
            Date: Datedata,
            Time: Timedata
        }
    }


    if(typeof props.date != 'undefined'){
        const Timeline_Data = props.date.split(/T|Z/)

        const Target_Data = {
            Year: Timeline_Data[0].split('-')[0],
            Month: Timeline_Data[0].split('-')[1],
            Date: Timeline_Data[0].split('-')[2],
            Time: Timeline_Data[1]
        }

        console.log(Target_Data)

        CompareProcess(Target_Data).then((result)=>{
            
            if(result.Year.Gap != 0){
                switch(result.Year.status){
                    case('+'):
                    updatedata(`${result.Year.Gap} years ahead`)
                    return;

                    case('-'):
                    updatedata(`${result.Year.Gap} years ago`)
                    return;

                    default:
                    return;
                }
            }
            else{
                if(result.Month.Gap != 0){
                    console.log(result.Month.Gap)
                    switch(result.Month.status){
                        case('+'):
                        updatedata(`${result.Month.Gap} months ahead`)
                        return;
    
                        case('-'):
                        updatedata(`${result.Month.Gap} months ago`);
                        return;
    
                        default:
                        return;
                    }
                }
                else{
                    if(result.Date.Gap != 0){
                        switch(result.Date.status){
                            case('+'):
                            updatedata(`${result.Date.Gap} days ahead`);
                            return;
        
                            case('-'):
                            updatedata(`${result.Date.Gap} days ago`);
                            return;
        
                            default:
                            return;
                        }
                    }
                }
            
            }

            
        })

    }

    else{
        console.log(props)
        return;
    }

    console.log(Datedata)
    return Datedata;

}

export default DateCalculator;
