import React, { useEffect, useState } from "react";

import Homepage_Btn from './Homepage_Btn.js'
import DateCalculator from "./DateCalculator.js";

import { Buffer } from "buffer";

import ArrowBtn from '../../../../src/1_image_or_icon/Arrow_Button_DownAhead.png'

export const My_Reviews = (props) => {

    const [element ,updateREVIEWS] = useState([])
    const [USERNAME ,updateUSERNAME] = useState({name: ''})

    useEffect(()=>{

        let USER;
        let STATUS;
        
        if(props.id){
            USER = props.id.userinfo
            STATUS = props.id.status
        }

        switch(STATUS){
            case('unverified'):
            return;

            case('verified'):
            Load_Review(USER.id,USER.name)
            return;

            default:
            return;
        }

    },[props])

    const TextDispenser = (props) => {

        let TextContent = props.element.user_post_text
        
        return <React.Fragment>
        
            <div
            style={{
                backgroundColor: 'white',
                borderRadius: '10px',
                width: '80%',
                height: '5vh',
                overflow: 'hidden',
                padding: '1vh 2.5vw 4vh 2.5vw',
                justifySelf: 'center',
                letterSpacing: '1.5px',
                wordSpacing: '1px',
                lineHeight: '3.5vh',
                whiteSpace: 'pre-wrap',
                overflowWrap: 'break-word',
                maskImage: 'linear-gradient(black, transparent)',
                transition: 'height 0.3s ease-in-out'
            }}
            >
                {TextContent}
            </div>
                <img
                src={ArrowBtn}
                style={{
                    position: 'relative',
                    width: '5vw',
                    bottom: '4vh',
                    justifySelf: 'center',
                    transform: 'rotate(0deg)',
                    transition: 'transform 0.3s ease-in-out'
                }}
                onClick={(e)=>{
                    const textDiv = e.target.previousElementSibling;
                    switch(textDiv.style.maskImage){
                        case(''):
                        textDiv.style.maskImage = 'linear-gradient(black, transparent)';
                        textDiv.style.height = '5vh';
                        e.target.style.transform = 'rotate(0deg)';
                        break;

                        case('linear-gradient(black, transparent)'):
                        textDiv.style.maskImage = '';
                        textDiv.style.height = `${textDiv.scrollHeight}px`;
                        e.target.style.transform = 'rotate(180deg)';
                        break;
                    }
                }}
                ></img>

            </React.Fragment>
        
    }

    const Review_Container = () => {
        const [selectedImageIndex, setSelectedImageIndex] = useState(-1);
        const [showSlider, setShowSlider] = useState(false);

        return <React.Fragment>
            {showSlider && (
                <div
                style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    backgroundColor: 'rgba(0,0,0,0.8)',
                    zIndex: 1000,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    alignItems: 'center'
                }}
                onClick={() => {
                    setShowSlider(false);
                    setSelectedImageIndex(-1);
                }}
                >
                    <div
                    style={{
                        position: 'relative',
                        width: '80%',
                        maxWidth: '600px',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center'
                    }}
                    onClick={(e) => e.stopPropagation()}
                    >
                        <button
                        style={{
                            position: 'absolute',
                            left: '10px',
                            background: 'white',
                            border: 'none',
                            borderRadius: '50%',
                            padding: '10px',
                            cursor: 'pointer'
                        }}
                        onClick={() => {
                            const newIndex = selectedImageIndex === 0 ? 
                                PICTURES_ARRAY.length - 1 : selectedImageIndex - 1;
                            setSelectedImageIndex(newIndex);
                        }}
                        >
                            ←
                        </button>

                        <img
                        src={PICTURES_ARRAY[selectedImageIndex]}
                        style={{
                            maxWidth: '100%',
                            maxHeight: '80vh',
                            objectFit: 'contain'
                        }}
                        />

                        <button
                        style={{
                            position: 'absolute',
                            right: '10px',
                            background: 'white',
                            border: 'none',
                            borderRadius: '50%',
                            padding: '10px',
                            cursor: 'pointer'
                        }}
                        onClick={() => {
                            const newIndex = selectedImageIndex === PICTURES_ARRAY.length - 1 ? 
                                0 : selectedImageIndex + 1;
                            setSelectedImageIndex(newIndex);
                        }}
                        >
                            →
                        </button>
                    </div>
                </div>
            )}

            <div
            style={{
                width:'95%',
                display: 'grid',
                gridTemplateColumns: '(repeat 1, 1fr)',
                rowGap: '2vh',
                padding: '2vh 0vw'
            }}
            >
                {element.map((element, index)=>{

                    const PICTURES_BINARY_ARRAY = element.user_post_pictures
                    let PICTURES_ARRAY = [];

                    PICTURES_BINARY_ARRAY.forEach((element, index)=>{
                        const DATA = Buffer.from(element)
                        let encoded_imagesrc = DATA.toString('base64')
                        encoded_imagesrc = `data:image/jpg;base64,${encoded_imagesrc}`;

                        PICTURES_ARRAY = [...PICTURES_ARRAY, encoded_imagesrc]
                    })

                    return <div
                    style={{
                        display: 'grid',
                        gridTemplateColumns: '(repeat 1, 1fr)',
                        rowGap: '1vh',

                        border:'black solid 3px',
                        borderRadius: '28px',

                        padding: '1.5vh 3vw 3vh 3vw'
                    }}
                    >
                    
                    <div
                        style={{
                            justifySelf:'center'
                        }}
                    >
                        {element.placename}
                    </div>

                    <TextDispenser
                    element={element}
                    />

                    <div
                    style={{
                        position:'relative',
                        bottom: '15px',
                        justifySelf: 'center'
                    }}
                    >
                        {`작성자: ${USERNAME.name}`}
                    </div>

                    <div
                    style={{
                        fontSize:'15px',
                        justifySelf: 'center',
                        backgroundColor: 'white',
                        borderRadius: '15px',
                        padding: '0.25vh 2vw'
                    }}
                    >
                        Created
                    </div>

                    <div
                    style={{
                        width: '70%',
                        justifySelf: 'center',
                        borderBottom: 'white solid 5px',
                        textAlign: 'center'
                    }}
                    >
                        <DateCalculator
                        date={element.created_at}
                        />
                    </div>

                    <img
                    src=''
                    style={{
                        display:'none',
                        width: '80%',
                        aspectRatio: '1/1',
                        objectFit: 'cover',
                        justifySelf: 'center',
                        border: 'white solid 3px',
                        borderRadius: '10px'
                    }}
                    alt="let"
                    >
                    </img>

                    <div
                    style={{
                        width:'80%',
                        display: 'grid',
                        gridTemplateColumns: 'repeat(3, 1fr)',
                        justifyItems: 'center',
                        justifySelf: 'center',
                        alignItems: 'center',
                        border: 'black solid 3px',
                        borderRadius:'15px',
                        padding: '1.5vh 2vw',
                        columnGap: '2vw',
                        rowGap: '1.5vh'
                    }}
                    >
                        {PICTURES_ARRAY.map((pic, picIndex)=>{
                            return <img
                            src={pic}
                            style={{
                                width: '80%',
                                aspectRatio: '1',
                                border:'white solid 3px',
                                borderRadius: '10px',
                                cursor: 'pointer'
                            }}
                            onClick={() => {
                                setSelectedImageIndex(picIndex);
                                setShowSlider(true);
                            }}
                            />
                        })}
                    </div>

                    </div>

                })}

            </div>

        </React.Fragment>

    }

    const Load_Review = async (user_id, username) => {
        await fetch(`/usercreation/read_reviews/${user_id}`).then( async (result)=>{
            const review_data = await result.json()
            updateREVIEWS(review_data.result)
            updateUSERNAME({name: username})
        })
    }

    if(props.id){
        
        switch(props.id.status){
        case('verified'):
        return <React.Fragment>

        <div
        className="Personalize_Data_Page_Container"
        >

            <Homepage_Btn/>

            <div
            className="Personalized_Data_Container_Aligner"
            >

                <span
                className="Personalized_Data_Page_TextTitle"
                >
                My Reviews
                </span>
                <br></br>

            
                <Review_Container/>
                

            </div>

        </div>

    </React.Fragment>

    case('unverified'):
    return <React.Fragment>

            <div
            className="Personalize_Data_Page_Container"
            >

                <Homepage_Btn/>

                <div
                className="Personalized_Data_Container_Aligner"
                >

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    My Reviews
                    </span>
                    <br></br>

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    This Page
                    <br></br>
                    is Preparing
                    </span>                    

                </div>

            </div>

        </React.Fragment>


    }


    }
}

export default My_Reviews
