import React, { useEffect, useState } from "react";

import Homepage_Btn from './Homepage_Btn.js'
import DateCalculator from "./DateCalculator.js";

import { Buffer } from "buffer";

import ArrowBtn from '../../../../src/1_image_or_icon/Arrow_Button_DownAhead.png'

export const My_Reviews = (props) => {

    const [element ,updateREVIEWS] = useState([])
    const [USERNAME ,updateUSERNAME] = useState({name: ''})
    const [BLINDER_STATUS ,updateBLINDER] = useState({
        status: 'ONLY_TITLE'
    })

    useEffect(()=>{

        let USER;
        let STATUS;
        
        if(props.id){
            USER = props.id.userinfo
            STATUS = props.id.status
        }

        switch(STATUS){
            case('unverified'):
            return;

            case('verified'):
            Load_Review(USER.id,USER.name)
            return;

            default:
            return;
        }

    },[props])

    const TextExtender = (status, text) => {
        switch(status){
            case('ONLY_TITLE'):
            return text.slice(0,10);
            case(`EXTENDED`):
            return text.slice(0)
        }
    }

    const TextDispenser = (props) => {

        let index = 0;
        let TextPrettier;
        let TextContent = props.element.user_post_text

        switch(BLINDER_STATUS.status){
            case('ONLY_TITLE'):
            TextPrettier = TextContent.slice(0,12)
            break;

            case('EXTENDED'):

            for(index=0; index<Math.floor(TextContent.length/12); index++){

                let Start = index*12
                let End = (index+1)*12
    
                TextPrettier = <React.Fragment>
                                    {TextPrettier}
                                    {TextContent.slice(Start,End)}
                                    <br></br>
                                    <br></br>
                                </React.Fragment>
            }
            if(index === 0){
                TextPrettier = <React.Fragment>
                                    {TextContent.slice(0)}
                                </React.Fragment>
            }
            break;

        }



        return TextPrettier
        
        
    }

    const Review_Container = () => {

        return <React.Fragment>

            <div
            style={{
                width:'95%',

                display: 'grid',
                gridTemplateColumns: '(repeat 1, 1fr)',
                rowGap: '2vh',

                padding: '2vh 0vw'
            }}
            >

            {element.map((element, index)=>{

                const PICTURES_BINARY_ARRAY = element.user_post_pictures
                let PICTURES_ARRAY = [];

                PICTURES_BINARY_ARRAY.forEach((element, index)=>{
                    const DATA = Buffer.from(element)
                    let encoded_imagesrc = DATA.toString('base64')
                    encoded_imagesrc = `data:image/jpg;base64,${encoded_imagesrc}`;

                    PICTURES_ARRAY = [...PICTURES_ARRAY, encoded_imagesrc]
                })

                return <div
                style={{
                    display: 'grid',
                    gridTemplateColumns: '(repeat 1, 1fr)',
                    rowGap: '1vh',

                    border:'black solid 3px',
                    borderRadius: '28px',

                    padding: '1.5vh 3vw 3vh 3vw'
                }}
                >
                
                <div>
                    {element.placename}
                </div>

                <div
                style={{
                    backgroundColor: 'white',
                    borderRadius: '10px',

                    width: '80%',
                    padding: '1vh 2.5vw 4vh 2.5vw',

                    justifySelf: 'center',
                    
                    letterSpacing: '1.5px',
                    wordSpacing: '-3px',
                    lineHeight: '3.5vh',
                    whiteSpace: 'pre-wrap',
                    // wordWrap: 'break-word',
                    maskImage: 'linear-gradient(black, transparent)'
                }}
                >
                <TextDispenser
                element={element}
                />
                </div>

            <img
            src={ArrowBtn}
            style={{
                position: 'relative',

                width: '5vw',
                left: '23vw',
                bottom: '4vh'
            }}
            onClick={(e)=>{
                console.log()
                switch(BLINDER_STATUS.status){
                    case('ONLY_TITLE'):
                    updateBLINDER({
                        status:'EXTENDED'
                    })
                    break;

                    case('EXTENDED'):
                    updateBLINDER({
                        status:'ONLY_TITLE'
                    })
                    break;
                }
            }}

            ></img>

                <div
                style={{
                    position:'relative',
                    right: '14px',
                    justifySelf: 'end'
                }}
                >
                    {`작성자: ${USERNAME.name}`}
                </div>

                <div
                style={{
                    position:'relative',
                    right: '14px',
                    justifySelf: 'end'
                }}
                >
                    Created
                </div>

                <div
                style={{
                    position:'relative',
                    right: '14px',
                    bottom: '7px',

                    justifySelf: 'end'
                }}
                >
                    <DateCalculator
                    date={element.created_at}
                    />
                </div>

                <div
                style={{
                    
                    display: 'grid',
                    gridTemplateColumns: 'repeat(3, 1fr)',
                    justifyItems: 'center',
                    alignItems: 'center'
                }}
                >
                    {PICTURES_ARRAY.map((element, index)=>{

                        return <img
                        src={`${element}`}
                        style={{
                            width: '10vw'
                        }}

                        >
                        </img>

                    })}
                </div>

                </div>

            })}

            </div>

        </React.Fragment>

    }

    const Load_Review = async (user_id, username) => {
        await fetch(`/usercreation/read_reviews/${user_id}`).then( async (result)=>{
            const review_data = await result.json()
            updateREVIEWS(review_data.result)
            updateUSERNAME({name: username})
        })
    }

    if(props.id){
        
        switch(props.id.status){
        case('verified'):
        return <React.Fragment>

        <div
        className="Personalize_Data_Page_Container"
        >

            <Homepage_Btn/>

            <div
            className="Personalized_Data_Container_Aligner"
            >

                <span
                className="Personalized_Data_Page_TextTitle"
                >
                My Reviews
                </span>
                <br></br>

            
                <Review_Container/>
                

            </div>

        </div>

    </React.Fragment>

    case('unverified'):
    return <React.Fragment>

            <div
            className="Personalize_Data_Page_Container"
            >

                <Homepage_Btn/>

                <div
                className="Personalized_Data_Container_Aligner"
                >

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    My Reviews
                    </span>
                    <br></br>

                    <span
                    className="Personalized_Data_Page_TextTitle"
                    >
                    This Page
                    <br></br>
                    is Preparing
                    </span>                    

                </div>

            </div>

        </React.Fragment>


    }


    }
}

export default My_Reviews
