import React, { useState } from "react";

export const Detail_Image_Section = (props) => {

    const [FirstTouch, updateStart] = useState(0)
    const [LastTouch, updateLast] = useState(0)
    const [Distance, updateDistance] = useState(0)
    

    const photo_dispenser = (input) => {

        switch(typeof input){
            case('object'):
            // console.log(input[0].getUrl())
            return <div
                    style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent:'start',
                        width:'60vw',
                        overflow: 'scroll',
                        aspectRatio: '1',
                        border: 'black solid 3.5px',
                        borderRadius: '15px'
                    }}
                    
                    

                    onTouchStart={(e)=>{
                        const touches = e.changedTouches[0].pageX;
                        updateStart(touches)

                    }}

                    onTouchEnd={(e)=>{
                        const touches = e.changedTouches[0].pageX
                        updateLast(touches)

                        const Arrow = FirstTouch - LastTouch
                        switch(typeof e.target.parentNode.childNodes.length){

                                case('number'):

                                const Length = e.target.parentNode.childNodes.length

                                if(Arrow>100){
                                updateDistance(Distance+e.target.offsetWidth)

                                e.target.parentNode.scrollTo({
                                    top:0,
                                    left:Distance+e.target.offsetWidth,
                                    behavior:'smooth'
                                })
                                
                                if(Distance>e.target.offsetWidth*(Length-1)){
                                    updateDistance(e.target.offsetWidth*Length)
                                }
                                return;}

                                if(Arrow<-100){
                                updateDistance(Distance-e.target.offsetWidth)

                                e.target.parentNode.scrollTo({
                                    top:0,
                                    left:Distance-e.target.offsetWidth,
                                    behavior:'smooth'
                                })

                                if(Distance<1){
                                    updateDistance(0)
                                }
                                return;}
                                
                                if(-100<Arrow<100){
                                    
                                e.target.parentNode.scrollTo({
                                    top:0,
                                    left:Distance,
                                    behavior:'smooth'
                                    })}
                        
                                case('undefined'): return;
                        }

                        }
                    }

                    >

                        {input.map((element)=>{
                        return <img
                        style={{
                            width:'60vw',
                            objectFit: 'cover'
                        }}
                        src={element.getUrl()}
                        >
                        </img>
                        })}

                    </div>

            case('undefined'):
            return;
        }

    }

    const photos_Dot_Index = (input) => {
        switch(typeof input){
            case('object'):
            
            return <React.Fragment>

            <div>
            &#8226;
            &#8226;
            &#8226;
            </div>

            </React.Fragment>
        }
    }

    return <React.Fragment>

    {photo_dispenser(props.photos)}
    {photos_Dot_Index(props.photos)}

    </React.Fragment>

}

export default Detail_Image_Section
