import React from "react";
import { useState, useEffect } from "react";
import { Link, useLocation } from 'react-router-dom';

import Personalized_data_progresser from "../TabNevBar_Opened/Personalized_data_progresser"
import Personalized_data_ProfileSection from "../TabNevBar_Opened/Personalized_data_ProfileSection"
import Personalized_data_progresser_Closed from "../TabNevBar_Closed/Personalized_data_progresser";

import ToggleButton_img from '../../../1_image_or_icon/Door_icon.jpg'


export const OnOff_Toggler = (props) => {

    const [ TabNevBar_Status, updateOnnOff] = useState('opened')
    
    useEffect(()=>{
        switch( TabNevBar_Status ){
            case ('closed'): 
            ConvertStructure('closed')
            updateOnnOff('opened')
            return;

            case ('opened'): 
            ConvertStructure('opened')            
            updateOnnOff('closed')
            return;
        }
    },[])

    const ConvertStructure = (status) => {
        
        switch(status){

            case ('opened'):
                document.getElementById('Personalized_data_Container').style.width = `25vw`                    
                document.getElementById('ToggleButton').style.marginLeft = `18vw`
                return;

            case ('closed'):
                document.getElementById('Personalized_data_Container').style.width = `7vw`                
                document.getElementById('ToggleButton').style.marginLeft = '0px'
                return; 
            }

    }

    const TabNevBar_Toggler = () =>{
        switch(TabNevBar_Status){

            case ('closed'): 
            ConvertStructure(TabNevBar_Status)
            updateOnnOff('opened')
            return;

            case ('opened'): 
            ConvertStructure(TabNevBar_Status)            
            updateOnnOff('closed')
            return;
        }
    }


    switch(TabNevBar_Status){

    case('opened'): return <React.Fragment>

            <div id="Personalized_data_Container">
                <img
                id='ToogleButton'
                className={props.className}
                src={ToggleButton_img}
                onClick={TabNevBar_Toggler}
                ></img>

                <Personalized_data_progresser_Closed/>
            </div>
        </React.Fragment>

    case('closed'): return <React.Fragment>

            <div id="Personalized_data_Container">
                <img
                id='ToogleButton'
                className={props.className}
                src={ToggleButton_img}
                onClick={TabNevBar_Toggler}
                ></img>

                <Personalized_data_ProfileSection id={props.id}/>
                <Personalized_data_progresser id={props.id}/>
            </div>
        </React.Fragment>

    }

}

export default OnOff_Toggler;
