import React from "react";
import { useState } from "react";
import { Link, useLocation } from 'react-router-dom';

import ToggleButton_img from '../../../1_image_or_icon/Door_icon.jpg'

export const OnOff_Toggler = () => {

    const [ TabNevBar_Status, updateOnnOff] = useState('closed')
    const [ TabNevBar_Toggler_Url, updateToggler] = useState('closed')

    const ConvertStructure = (status, tab) => {
        
        switch(status){

            case ('closed'):
                tab.target.style.marginLeft = `18vw`
                document.getElementById('Personalized_data_Container').style.width = `25vw`                    
                return;

            case ('opened'):
                tab.target.style.marginLeft = '0px'
                document.getElementById('Personalized_data_Container').style.width = `7vw`                
                return; 
            }

    }

    const TabNevBar_Toggler = (tab) =>{
        switch(TabNevBar_Status){

            case ('closed'): 
            ConvertStructure(TabNevBar_Status, tab)
            updateOnnOff('opened')
            updateToggler('opened')
            return;

            case ('opened'): 
            ConvertStructure(TabNevBar_Status, tab)            
            updateOnnOff('closed')
            updateToggler('closed')
            return;
        }
    }

    return <React.Fragment>

        <Link to={`/${TabNevBar_Toggler_Url}`}>
        <img
        id="ToggleButton"
        src={ToggleButton_img}
        onClick={TabNevBar_Toggler}
        ></img>
        </Link>

    </React.Fragment>

}

export default OnOff_Toggler;
