import React from "react";
import { useState } from "react";
import { Link } from 'react-router-dom';

import ToggleButton_img from '../../1_image_or_icon/Door_icon.jpg'

export const OnOff_Toggler = () => {

    const [ TabNevBar_Status, updateOnnOff] = useState('closed')

    const TabNevBar_Toggler = (tab) =>{
        switch(TabNevBar_Status){
            case ('closed'): 
            updateOnnOff('opened')
            tab.target.style.padding = 0
            document.getElementById('Personalized_data_Container').style.width = `0px`
            return;

            case ('opened'): 
            updateOnnOff('closed')
            tab.target.style.paddingLeft = `18vw`
            return;
        }
    }

    return <React.Fragment>

        <Link to={`/${TabNevBar_Status}`}>
        <img
        id="ToggleButton"
        src={ToggleButton_img}
        onClick={TabNevBar_Toggler}
        ></img>
        </Link>

    </React.Fragment>

}

export default OnOff_Toggler;
