import React from "react";

import { useState, useEffect } from "react";
import { useRef } from "react";

export const Keyword_popup = () => {

    const ref = useRef()
    let Url = new URLSearchParams (document.location.search)
    let Keyword_Cotainer = []

    const [ Keyword_Added_URL , updateSegment ] = useState('')

    useEffect(()=>{

    // const Keyword_Matched = document.getElementById('Keyword_popup_option')
    const Keyword_Matched = document.getElementById('Keyword_popup_option').textContent
    const Text = Url.get(`${Keyword_Matched}`)
    console.log(Text)

    },[Keyword_Added_URL])

    const Appending_Keyword = (Keyword_Current_Option, Keyword_Input) => {

        let Keyword = Url.get(Keyword_Current_Option)
        
        switch(typeof Keyword){

            case('object'):
                Keyword_Cotainer = []
                Keyword_Cotainer.push(Keyword_Input)
                Url.set(`${Keyword_Current_Option}`,`${Keyword_Cotainer}`)
                alert(`${Keyword_Input} got updated at ${Keyword_Current_Option}`)
                return;

            case('string'): Inspectiong_Keyword(Keyword_Input, Keyword_Cotainer, Keyword_Current_Option)
                return;
            }

        }

        const Inspectiong_Keyword = (Keyword, Keyword_Cotainer, Keyword_Current_Option) => {

            switch(Keyword_Cotainer.includes(Keyword)){
                
                    case(true):
                        alert(`${Keyword} is already updated at ${Keyword_Current_Option}`)
                        return;   

                    case(false):
                        Keyword_Cotainer.push(Keyword)
                        Url.set(`${Keyword_Current_Option}`,`${Keyword_Cotainer}`)
                        alert(`${Keyword} got updated at ${Keyword_Current_Option}`)
                        return;
            }

        }

        const Close_popup = () => {

            const Keyword_style = document.getElementById('Keyword_popup') 
            Keyword_style.id = 'Keyword_popup_Closed'

        }

        const Appending_Keyword_ForUI = (Text, option) => {

            const Keyword_Appender = document.getElementById(`AddedOption_${option.textContent}`)
            const Keyword = document.createElement('span')
            Keyword.textContent = `${Text}`
            Keyword_Appender.append(Keyword)

        }

        

    return <div id='Keyword_popup_Closed'>
    <div id='Keyword_popup_option'>Keyword Matched To</div>
    <div>Keyword to Add</div>

    <input ref={ref} id="Keyword_popup_input" ></input>
    <br></br>
    
    <button id="Keyword_popup_button" onClick={
        (tab)=>{

            const Keyword_Matched = document.getElementById('Keyword_popup_option')

            Appending_Keyword(Keyword_Matched.textContent, ref.current.value)

            console.log(Url.toString())

            Close_popup()
            
            updateSegment(Url.toString())
        }
    }>Add</button>
    </div>

}

export default Keyword_popup
