import React, { useEffect, useState } from "react";

import {APIProvider, Map} from '@vis.gl/react-google-maps'

export const Google_Map = () => {

const INITIAL_CAMERA = {
        zoom : 15,
        center : {lat: 40.7, lng: -74}
}

const [cameraProps, setCameraProps] = useState(INITIAL_CAMERA);

    const Location_Loaded_Successfully = (position) => {
        let latitude = position.coords.latitude;
        let longitude = position.coords.longitude;
    
        setCameraProps({
            zoom: 15, 
            center: {lat: latitude, lng: longitude}
    })}

    const Location_Loading = () => {
        // if (navigator.geolocation) {
     
        //     navigator.geolocation.getCurrentPosition(
                
        //     Location_Loaded_Successfully
    
        //     );
        // }

        switch(navigator.geolocation){
            case(true): 
                navigator.geolocation.getCurrentPosition( Location_Loaded_Successfully )
                return;
            case(false):
                return;
            }
    }

useEffect(()=>{

    Location_Loading()
    
},[])

return <React.Fragment>

            <APIProvider apiKey={`${process.env.REACT_APP_API}`}>

                <Map
                
                    style={ 
                        { width:'inherit',
                        height:'20vh',
                        backgroundColor:'white',
                        margin: '3vh 6vw 0 10vw',
                        border: 'black solid 5px'
                        }}
                    
                    {...cameraProps} 
                    />

            </APIProvider>


</React.Fragment>

}

export default Google_Map
