import React, { useState } from "react";

export const Detail_Image_Section = (props) => {

    const [FirstTouch, updateStart] = useState(0)
    const [LastTouch, updateLast] = useState(0)
    const [Distance, updateDistance] = useState(0)
    

    const photo_dispenser = (input) => {

        switch(typeof input){
            case('object'):
            // console.log(input[0].getUrl())
            return <div
                    style={{
                        display: 'flex',
                        flexDirection: 'row',
                        justifyContent:'start',
                        width:'60vw',
                        overflow: 'scroll',
                        aspectRatio: '1',
                        border: 'black solid 3.5px',
                        borderRadius: '15px'
                    }}
                    
                    

                    onTouchStart={(e)=>{
                        const touches = e.changedTouches[0].pageX;
                        updateStart(touches)

                    }}

                    onTouchEnd={(e)=>{
                        const touches = e.changedTouches[0].pageX
                        updateLast(touches)

                        const Arrow = FirstTouch - LastTouch

                            if(Arrow>0){
                            console.log(Distance)
                            updateDistance(Distance+e.target.offsetWidth)

                            e.target.parentNode.scrollTo({
                                top:0,
                                left:Distance+e.target.offsetWidth,
                                behavior:'smooth'
                            })
                            if(Distance>e.target.offsetWidth*9){
                                updateDistance(e.target.offsetWidth*8)
                            }
                            return;}

                            if(Arrow<0){
                            console.log(Distance)
                            updateDistance(Distance-e.target.offsetWidth)

                            e.target.parentNode.scrollTo({
                                top:0,
                                left:Distance-e.target.offsetWidth,
                                behavior:'smooth'
                            })

                            if(Distance<1){
                                updateDistance(0)
                            }
                            return;}

                        }
                    }

                    >

                        {input.map((element)=>{
                        return <img
                        style={{
                            width:'60vw',
                            objectFit: 'cover'
                        }}
                        src={element.getUrl()}
                        >
                        </img>
                        })}

                </div>

            case('undefined'):
            return;
        }

    }

    return <React.Fragment>

    {photo_dispenser(props.photos)}

    </React.Fragment>

}

export default Detail_Image_Section
